#this gets the pair address
def getPair(factory, addy1, addy2):
    factory_contract2 = web3.eth.contract(address=factory, abi=factory2_abi)
    pair_a = factory_contract2.functions.getPair(addy1, addy2).call()
    getPrice(pair_a)
    return(pair_a)

#this gets the price of one coin wrt another by calculating pool reserve
def getPrice(pair_a):
    pair_contract = web3.eth.contract(address=pair_a, abi=ABI3)
    (reserve0, reserve1, blockTimestampLast) = pair_contract.functions.getReserves().call()
    price = (reserve1*1000000000000)/reserve0

    print(str(price) + ' is price of Aurora in USDC')
    return(price)

